import { Box, Button, Dialog,TextField, Typography , styled} from '@mui/material'
import React from 'react'

const Component = styled(Box)`
  display:flex;
  height:70vh;
  width:90vh;

`
const Image = styled(Box)`
 background:#2874f0 url(https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png
) no-repeat center 85%;
 height:100%;
 width:60%;
  padding:25px 40px;
  color:white
`

const LoginDialog = (props) => {
  const handleClose =() =>{
    props.setOpen(false)
  }
  return (
   <>
    <Dialog open={props.openDialog} onClose={handleClose}>
      <Component>
        <Image>
          <Typography variant="h5" >Login</Typography>
          <Typography style={{marginTop:"20px"}}>Get access to your Orders, Wishlist and Recommendations</Typography>
        </Image>
        <Box>
        <TextField variant='standard' label="Enter Email/Number"/>
        <TextField variant='standard' label="Enter Password"/>
        <TextField>By continuing, you agree to Flipkart's Terms of Use and Privacy Policy.</TextField>
        <Button>Login</Button>
        <Typography>OR</Typography>
        <Button>Request OTP</Button>
        <Typography>New to Flipkart? Create an account</Typography>
        </Box>
      </Component>
    </Dialog>
   </>
  )
}

export default LoginDialog
