import {
  Box,
  Button,
  Dialog,
  TextField,
  Typography,
  styled,
} from "@mui/material";
import React from "react";

const Component = styled(Box)`
  display: flex;
  height: 75vh;
  width: 90vh;
`;
const Image = styled(Box)`
  background: #2874f0
    url(https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png)
    no-repeat center 85%;
  height: 100%;
  width: 65%;
  padding: 25px 35px;
  color: white;
`;
const Wrapper = styled(Box)`
  display: flex;
  flex-direction: column;
  padding: 25px 40px;
`;
const LoginButton = styled(Button)`
  text-transform: none;
  background-color: #fb641b;
  border-radius: 2px;
  color: #fff;
  height: 48px;
`;
const RequestOTP = styled(Button)`
  text-transform: none;
  background-color: #fff;
  color: #2874f0;
  height: 48px;
  box-shadow: 0 2px 4px 0 rgb(0, 0, 0, 20%);
`;

const LoginDialog = (props) => {
  const handleClose = () => {
    props.setOpen(false);
  };
  return (
    <>
      <Dialog open={props.openDialog} onClose={handleClose}>
        <Component>
          <Image>
            <Typography variant="h5">Login</Typography>
            <Typography style={{ marginTop: "20px", letterSpacing: "1px" }}>
              Get access to your Orders, Wishlist and Recommendations
            </Typography>
          </Image>
          {true ? (
            <Wrapper>
              <TextField
                style={{ marginTop: "20px" }}
                variant="standard"
                label="Enter Email/Number"
              />
              <TextField
                style={{ marginTop: "20px" }}
                variant="standard"
                label="Enter Password"
              />
              <Typography
                style={{
                  marginTop: "20px",
                  fontSize: "12px",
                  color: "#878787",
                }}
              >
                By continuing, you agree to Flipkart's
                <Typography
                  component="span"
                  style={{ fontSize: "12px", color: "#2874f0" }}
                >
                  Terms of Use
                </Typography>
                and
                <Typography
                  component="span"
                  style={{ fontSize: "12px", color: "#2874f0" }}
                >
                  Privacy Policy.
                </Typography>
              </Typography>
              <LoginButton style={{ marginTop: "20px" }}>Login</LoginButton>
              <Typography className="text-center mt-2">OR</Typography>
              <RequestOTP style={{ marginTop: "20px" }}>Request OTP</RequestOTP>
              <Typography
                style={{
                  fontSize: "14px",
                  marginTop: "45px",
                  fontWeight: "500",
                }}
              >
                New to Flipkart? Create an account
              </Typography>
            </Wrapper>
          ) : (
            <Wrapper>
              <TextField
                style={{ marginTop: "20px" }}
                variant="standard"
                label="Enter FirstName"
              />
              <TextField
                style={{ marginTop: "20px" }}
                variant="standard"
                label="Enter LastName"
              />
              <TextField
                style={{ marginTop: "20px" }}
                variant="standard"
                label="Enter UserName"
              />
              <TextField
                style={{ marginTop: "20px" }}
                variant="standard"
                label="Enter Email"
              />
              <TextField
                style={{ marginTop: "20px" }}
                variant="standard"
                label="Enter Password"
              />
              <TextField
                style={{ marginTop: "20px" }}
                variant="standard"
                label="Enter Phone"
              />
            
              <LoginButton style={{ marginTop: "20px" }}>Continue</LoginButton>
              <Typography
                style={{
                  fontSize: "14px",
                  marginTop: "45px",
                  fontWeight: "500",
                }}
              >
                New to Flipkart? Create an account
              </Typography>
            </Wrapper>
          )}
        </Component>
      </Dialog>
    </>
  );
};

export default LoginDialog;
