import React from 'react';
import { styled } from '@mui/material/styles';
import { Box , Toolbar , AppBar, Typography, Button} from '@mui/material';
import Search from './Search';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

const HeaderStyled = styled(AppBar)`
  background: #2874f0; 
`;

const Component = styled(Box)`
margin-left:12%;
line-height:0;
`
const SubHeading = styled(Typography)`
font-size:10px;
font-style:italic;
`

const PlusImage = styled("img")({
    width: 10,
    height:10,
    marginLeft:4,
})

const Header = () => {
  return (
    <>
    <HeaderStyled>
    <Toolbar  style={{height:"54px",paddingBottom:"0px"}}>
        <Component>
            <img src='https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/flipkart-plus_8d85f4.png' alt="img..." style={{width:"75px"}} />
        <Box style={{display:"flex"}}>
            <SubHeading>Explore
                <Box component="span" style={{color:"#ffe500",marginLeft:"6px"}}>Plus</Box>
            </SubHeading>
            <PlusImage src="https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/plus_aef861.png" alt="img..."/>
        </Box>
        </Component>
        <Search></Search>
        <Box style={{display:"flex"}}>
          <Button variant="container" style={{background:"#fff" , color:"#2874f0", borderRadius:"0", marginLeft:"40px", width:"120px" ,height:"30px"}}>
            LOGIN
          </Button>
            <Typography style={{margin:" 0 30px"}}>
              MORE
            </Typography>
            <Typography>
              Become a Seller
            </Typography>
            <Box> Cart
            <ShoppingCartIcon></ShoppingCartIcon>
            </Box>
        </Box>
    </Toolbar>
    </HeaderStyled>

    </>
  )
}

export default Header;
