import {
  Box,
  Button,
  Dialog,
  TextField,
  Typography,
  styled,
} from "@mui/material";
import React from "react";

const Component = styled(Box)`
  display: flex;
  height: 75vh;
  width: 90vh;
`;
const Image = styled(Box)`
  background: #2874f0
    url(https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png)
    no-repeat center 85%;
  height: 100%;
  width: 65%;
  padding: 25px 35px;
  color: white;
  border-radius:2px
`;
const Wrapper = styled(Box)`
  display:flex;
  flex-direction:column;
  padding:25px 40px;
`;
const LoginButton=styled(Button)`
  text-transform:none;
  background-color:#fb641b;
  color:#fff;
  height:48px; 
`

const LoginDialog = (props) => {
  const handleClose = () => {
    props.setOpen(false);
  };
  return (
    <>
      <Dialog open={props.openDialog} onClose={handleClose}>
        <Component>
          <Image>
            <Typography variant="h5">Login</Typography>
            <Typography style={{ marginTop: "20px", letterSpacing: "1px" }}>
              Get access to your Orders, Wishlist and Recommendations
            </Typography>
          </Image>
          <Wrapper>
            <TextField style={{ marginTop: "20px" }} variant="standard" label="Enter Email/Number" />
            <TextField style={{ marginTop: "20px" }} variant="standard" label="Enter Password" />
            <Typography style={{ marginTop: "20px",fontSize:"12px",color:"#878787" }}>
              By continuing, you agree to Flipkart's <Typography component="span" style={{ fontSize:"12px",color:"#2874f0" }} >Terms of Use</Typography> and <Typography component="span" style={{ fontSize:"12px",color:"#2874f0" }} > Privacy Policy.</Typography>
            </Typography>
            <LoginButton style={{ marginTop: "20px"}}>Login</LoginButton>
            <Typography className="text-center mt-2">OR</Typography>
            <Button style={{ marginTop: "20px"}}>Request OTP</Button>
            <Typography style={{fontSize:"14px", marginTop:"45px", fontWeight:"500" }}>New to Flipkart? Create an account</Typography>
          </Wrapper>
        </Component>
      </Dialog>
    </>
  );
};

export default LoginDialog;
